model.metadata:
CREATE view PersonMatView ( name string, id integer
) OPTIONS (MATERIALIZED 'TRUE', UPDATABLE 'TRUE', MATERIALIZED_TABLE 'PersonMatCache.Person', "teiid_rel:MATVIEW_TTL" 60000, "teiid_rel:MATVIEW_BEFORE_LOAD_SCRIPT" 'execute PersonMatCache.native(''truncate cache'');', "teiid_rel:MATVIEW_LOAD_SCRIPT" 'insert into PersonMatCache.ST_Person (name, id) SELECT name, id from PersonInfoModel.PersonInfo', "teiid_rel:MATVIEW_AFTER_LOAD_SCRIPT" 'execute PersonMatCache.native(''swap cache names'');', "teiid_rel:MATVIEW_SHARE_SCOPE" 'NONE', "teiid_rel:ALLOW_MATVIEW_MANAGEMENT" 'true', "teiid_rel:MATVIEW_STATUS_TABLE" 'Example.status', "teiid_rel:MATVIEW_ONERROR_ACTION" 'THROW_EXCEPTION') AS SELECT name, id from PersonInfoModel.PersonInfo;
CREATE VIRTUAL PROCEDURE delete_by_key(IN "old_id" INTEGER, OUT result INTEGER) AS BEGIN DELETE FROM PersonMatCache.Person WHERE "id" = "old_id"; DELETE FROM PersonMatCache.ST_Person WHERE "id" = "old_id"; END;
